import UIKit

struct FavouritesConfigurator {

	static func createModule() -> UIViewController {
        var view: FavouritesViewController!

        let viewController = UIStoryboard.init(name: "Favourites", bundle: Bundle.main).instantiateInitialViewController()
        if viewController == nil {
            fatalError("Seems there is no initial view controller in Favourites.storyboard")
        }

        if viewController is UINavigationController {
            view = (viewController as? UINavigationController)?.viewControllers.first as? FavouritesViewController
        } else {
            view = viewController as? FavouritesViewController
        }

        let interactor = FavouritesInteractor()
        let router = FavouritesRouter()
        let presenter = FavouritesPresenter(interface: view, interactor: interactor, router: router)
        
        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view
        
        return viewController!
    }
}
