import UIKit

struct UserInfoConfigurator {

    static func createModule(with name: String) -> UIViewController {
        var view: UserInfoViewController!

        let viewController = UIStoryboard.init(name: "UserInfo", bundle: Bundle.main).instantiateInitialViewController()
        if viewController == nil {
            fatalError("Seems there is no initial view controller in UserInfo.storyboard")
        }

        if viewController is UINavigationController {
            view = (viewController as? UINavigationController)?.viewControllers.first as? UserInfoViewController
        } else {
            view = viewController as? UserInfoViewController
        }

        let interactor = UserInfoInteractor()
        let router = UserInfoRouter()
        let presenter = UserInfoPresenter(interface: view, interactor: interactor, router: router, name: name)
        
        view.presenter = presenter
        interactor.presenter = presenter
        router.viewController = view
        
        return viewController!
    }
}
